import { Injectable } from '@angular/core';
import { Product } from '../models/product.model';
import { Size } from '../models/size.model';

@Injectable({
  providedIn: 'root'
})
export class ProductsService {
  products: Product[] = [
    new Product(
      "Hermione Granger Plush",
      "Peluche certifiée Oeko-Tex, coton bio issu du commerce équitable, taille 30cm",
      "https://example.com/hermione.jpg",
      3000,
      0,
      false,
      new Date("2023-01-01"),
      [new Size("Medium", 30)]
    ),
    new Product(
      "Harry Potter Plush",
      "Peluche certifiée Oeko-Tex, coton bio issu du commerce équitable, taille 40cm",
      "https://example.com/harry.jpg",
      4000,
      0,
      false,
      new Date("2023-01-01"),
      [new Size("Large", 40)]
    ),
    // Ajoutez les autres peluches Harry Potter ici
  ];

  constructor() { }
  
  getAllProducts(): Product[] {
    return this.products;
  }
}
